import React, { Component } from "react";

class SingleEmail extends Component {
  constructor(props) {
    super(props);
  }

  toggleForward = () => {
    let toggleIndex = this.props.match.params.index;
    return toggleIndex + 1;
  };
  toggleBack = () => {
    let toggleIndex = this.props.match.params.index;
    return toggleIndex - 1;
  };
  render() {
    console.log(this.props);
    return (
      <div id='spec-email'>
        <button onClick={e => this.toggleForward}></button>
        <button onClick={e => this.toggleBack}></button>
      </div>
    );
  }
}

export default SingleEmail;
