import React from "react";

const SingleEmail = props => {
  let toggleIndex = props.match.params.index;
  const toggleForward = () => {
    return toggleIndex + 1;
  };
  const toggleBack = () => {
    return toggleIndex - 1;
  };
  console.log(toggleIndex);
  return (
    <div id='spec-email'>
      <button onClick={e => toggleForward}></button>
      <button onClick={e => toggleBack}></button>
      {props.mail[toggleIndex].info}
    </div>
  );
};

export default SingleEmail;
